mapper.select:
select bo.*, su.name as supplier, su.is_cold_business as supplierColdManage, su.can_special as supplierSpecialManage, sc.name as supplierContact, w.name as warehouse, o.value as shipMethod, o1.value as shipTool, o2.value as temperControl from buy_order bo left join supplier su on bo.supplier_id = su.id left join supplier_contact sc on bo.supplier_contact_id = sc.id left join warehouse w on w.id=bo.warehouse_id left join options o on o.id = bo.ship_method_id left join options o1 on o1.id = bo.ship_tool_id left join options o2 on o2.id = bo.temper_control_id where 1=1 and bo.status != 'DELETE'
and bo.supplier_id = #{supplierId, jdbcType=BIGINT}
and bo.status = #{status,jdbcType=VARCHAR}
and bo.created_time >= #{startDate,jdbcType=TIMESTAMP}
and #{endDate,jdbcType=TIMESTAMP} > bo.created_time
and bo.status in
#{item}
and bo.company_id = #{companyId,jdbcType=VARCHAR}

select d.goods_id as goodsId, count(b.id) as buyOrderCount, sum(quantity) as ongoingCount from buy_order b, buy_order_detail d where b.id = d.buy_order_id and b.status in ('INIT', 'CHECKING', 'CHECKED') and d.goods_id in
#{item}
group by d.goods_id
