mapper.select:
select t.id as id, t.sell_order_id as sellOrderId, t.repertory_id as repertoryId, t.quantity as quantity, t.fix_price as fixPrice, t.dis_price as disPrice, t.free as free, t.real_price as realPrice, t.single_price as singlePrice, t.amount as amount, t.tax_rate as taxRate, t.create_by as createBy, t.create_time as createTime, t.update_by as updateBy, t.update_time as updateTime, t.check_status as checkStatus, t.check_result as checkResult, t.check_date as checkDate, t.check_user as checkUser, c.name as customerName, s.create_order_date as createOrderDate, u.nickname as salerNickName, u.realname as salerRealName from sell_order_detail t, sell_order s, users u, customer c, repertory_info r where t.sell_order_id = s.id and s.customer_id = c.id and s.saler_id = u.id and t.repertory_id = r.id and s.company_id = #{companyId} and s.customer_id = #{customerId} and r.good_id in
#{item}
order by s.create_order_date desc
limit #{offset}, #{limit}

select
  distinct d.id
from sell_order s, sell_order_detail d
where s.id = d.sell_order_id
and s.id = #{sellOrderId}
and (d.check_status is null or d.check_status != 'OK');

select
distinct d.id
from sell_order s, sell_order_detail d
where s.id = d.sell_order_id
and s.id = #{sellOrderId}
and d.check_status = 'OK';
