select status, count(1) as count from sell_order where status != 'DELETE'
and company_id = #{companyId}
group by status

select date(create_time) as tradeDate, sum(total_amount) as amount from sell_order where status = 'SALE_CHECKED'
and company_id = #{companyId}
and create_time >= #{startDate}
and #{endDate} > create_time
group by tradeDate order by tradeDate

select g.name as goodsName, g.id as goodsId, sum(sod.amount) as amount from sell_order so, sell_order_detail sod, goods_info g, goods_detail gd where sod.sell_order_id = so.id and sod.goods_id=gd.id and gd.goods_info_id=g.id and so.status='SALE_CHECKED'
and so.company_id = #{companyId}
and so.create_time >= #{startDate}
and #{endDate} > so.create_time
group by g.id order by amount desc

select so.customer_id as customerId, c.name as customerName, c.account_amount as customerReceivable, count(distinct sod.sell_order_id) as orderCount, count(distinct sod.goods_id) as goodsCount, sum(sod.amount) as amount, min(so.create_time) as earliestOrderDate, max(so.create_time) as latestOrderDate from sell_order so, sell_order_detail sod, customer c where sod.sell_order_id = so.id and so.status='SALE_CHECKED' and so.customer_id = c.id
and so.company_id = #{companyId}
and so.customer_id = #{customerId}
and so.create_time >= #{startDate}
and #{endDate} > so.create_time
group by so.customer_id order by so.customer_id
