mapper.insert:
insert into sell_review_options (id, sell_detail_id, review_type, 
  review_status, review_comment, create_by, 
  create_time, update_by, update_time
  )
values (#{id,jdbcType=BIGINT}, #{sellDetailId,jdbcType=BIGINT}, #{reviewType,jdbcType=VARCHAR}, 
  #{reviewStatus,jdbcType=VARCHAR}, #{reviewComment,jdbcType=VARCHAR}, #{createBy,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select 
from sell_review_options
where sell_detail_id in

  #{item}


  and review_type = #{reviewType}

mapper.delete:
delete from sell_review_options where sell_detail_id in

  #{item}


  and review_type = #{reviewType}

mapper.select:
select DISTINCT d.id as detailId
from sell_order s, sell_order_detail d
left join sell_review_options o on d.id = o.sell_detail_id
where s.id = d.sell_order_id and s.id = #{sellOrderId}
and (o.review_status is null or o.review_status != 'OK') ;
