mapper.select:

select

from supplier
where company_id = #{companyId} and (
  name like #{pattern}
  or contact like #{pattern}
  or lower(pinyin) like lower(#{pattern})
)
order by id desc

mapper.update:
update supplier
  set account_amount = #{accountAmount}
where id = #{id}

mapper.select:
select
from supplier where enabled = 1 and company_id = #{companyId}
and (name like #{pattern} or contact_phone like #{pattern} or lower(pinyin) like lower(#{pattern}) OR contact like #{pattern} )
limit #{offset}, #{limit}
